import React,{useState,useEffect} from 'react'
import Axios from "axios"
import { Link } from 'react-router-dom'
import {useDispatch,useSelector} from "react-redux"
import './Home.css'
export default function Home() {
    const [arre,setArre]=useState([])
    const [select, setSelect]=useState([])
    const dispatch = useDispatch()
    useEffect(()=>{
      getStudent()
    }, [])

   function getStudent(){
    Axios.get("http://myjson.dit.upm.es/api/bins/f1r4")
      .then(ress=>{
        console.log(ress.data);
        setArre(ress.data)
        setSelect(ress.data)
      })
      .catch(err =>{
        console.log(err);
    })
    }
    const selectFun=(e)=>{
        let cat = select.filter((i)=>{
          return(i.category===e.target.value)
        })
        setArre(cat)
        // if(e.target.value==="all"){
        //   setArre(select)
        // }
      }
      const searchFun=(e)=>{
        let search = select.filter((i)=>{
          return(i.name.toLowerCase().includes((e.target.value).toLowerCase()))
        })
        setArre(search)
      }
      function addFun(e){
        dispatch({type:"additem",payload:e})
        // let massiv = []
        // let localDate = localStorage.getItem("massiv")
        // if(localDate){
        //   massiv = JSON.parse(localDate)
        //   massiv.push(e)
        //   localStorage.setItem("massiv",JSON.stringify(massiv))
        // }else{
        //   massiv.push(e)
        //   localStorage.setItem("massiv",JSON.stringify(massiv))
        // }
      }
  return (
    <div className='container '>
    <div className='row'>
    <div className='form__section'>
    <div className='select__section'>
      <select onChange={(e)=>selectFun(e)}  aria-label="Default select example">
        <option value="all">All</option>
        <option value="classic">Classic</option>
        <option value="Smart">Smart</option>
        <option value="compas">Compas</option>
        <option value="Sport">Sport</option>
        <option value="Quartz">Quartz</option>
      </select>
    </div>
    <div className='dan__gacha'>
      <input  placeholder='Dan' className='' type="text" />
      <label htmlFor=""><b>Dan</b></label>
      <input  placeholder='Gacha' className='' type="text" />
      <label htmlFor=""><b> Gacha</b></label>
    </div>
  </div>
  </div>
      <div className='sorch__section'>
        <input onChange={(e)=>searchFun(e)} placeholder='Search' type="search" className='form-control'/>
      </div>


    <div className='cards '>
      {
        (arre.length >0) ? (
        arre.map((e,index)=>{
            return(
                <div className='card main__card shadow col-4' key={index}>
                <img  src={e.img_src}  />
                <h3 className='my-2'>{e.name}</h3>
                <h4>Cost: {e.cost}$</h4>   
                <button onClick={()=>addFun(e)} className='btn btn-primary'>Sotib olish</button>
                </div>
                )
            })
          ):
          (
            <h4 >loading...</h4>
          )
      }
      </div>
      </div>

  )
}
