import React,{useState,useEffect} from 'react'
import Axios from "axios"
import { Link } from 'react-router-dom'
import {useDispatch,useSelector} from "react-redux"

export default function Home() {
    const [arre,setArre]=useState([])
    const [select, setSelect]=useState([])
    const dispatch = useDispatch()
    useEffect(()=>{
      getStudent()
    }, [])

   function getStudent(){
    Axios.get("http://myjson.dit.upm.es/api/bins/f1r4")
      .then(ress=>{
        console.log(ress.data);
        setArre(ress.data)
        setSelect(ress.data)
      })
      .catch(err =>{
        console.log(err);
    })
    }
  return (
    <div className='container'>
      <div className='form__section'>
        <div className='select__section'>
          <select  aria-label="Default select example">
            <option value="all">All</option>
            <option value="classic">Classic</option>
            <option value="Smart">Smart</option>
            <option value="compas">Compas</option>
            <option value="Sport">Sport</option>
            <option value="Quartz">Quartz</option>
          </select>

        <div className='dan__gacha'>
          <input  placeholder='Dan' className='' type="text" />
          <label htmlFor=""><b>Dan</b></label>
          <input  placeholder='Gacha' className='' type="text" />
          <label htmlFor=""><b> Gacha</b></label>
        </div>
        </div>
      </div>
      <div className='sorch__section'>
        <input  type="search" className='form-control'/>
      </div>


    <div className='cards'>
      {
        (arre.length >0) ? (
        arre.map((e,index)=>{
            return(
                <div className='card main__card shadow col-4' key={index}>
                <img className='my-2' src={e.img_src} alt="Rosim" />
                <h3 className='my-2'>{e.name}</h3>
                <h4>Cost: {e.cost}$</h4>   
                </div>
                )
            })
          ):
          (
            <h4 >loading...</h4>
          )
      }
      </div>
      </div>

  )
}
